import AnswersList from "../Components/AnswersList";
import QuestionItem from "../Components/QuestionItem";
import { json, useLoaderData } from "react-router-dom";

type Props = {};

const Answers = (props: Props) => {
  const data: any = useLoaderData();
  const question = data[0];
  const answers = data[1];
  console.log(answers[0])

  return (
    <>
      <QuestionItem event={question} />
      <ul>
        {answers.map((answer : any) => {
          <li>
            <div>
              <h1>{answer.user}</h1>
            </div>
          </li>
        })}
      </ul>
    </>
  );
};

export default Answers;

export async function loader({ req, params }: any) {
  const id = params.questionId;

  const response = await fetch(`http://localhost:8080/questions/${id}`);

  if (!response.ok) {
    throw json(
      { message: "Could not fetch answers for selected question." },
      { status: 500 }
    );
  } else {
    return response;
  }
}
