import classes from "./AnswerItem.module.css";

const AnswerItem = ({ answer }: any) => {
  return (
    <div className={classes.item}>
      <p>{answer.content}</p>
      <div className={classes.info}>
        <h4>{answer.user}</h4>
        <time>{answer.created}</time>
      </div>
    </div>
  );
};

export default AnswerItem;
